static gboolean
aa_authority_processor_dispatch_call (ThriftDispatchProcessor *dispatch_processor,
                                      ThriftProtocol *input_protocol,
                                      ThriftProtocol *output_protocol,
                                      gchar *method_name,
                                      gint32 sequence_id,
                                      GError **error)
{
  aa_authority_processor_process_function_def *process_function_def;
  gboolean dispatch_result = FALSE;

  aaAuthorityProcessor *self = AA_AUTHORITY_PROCESSOR (dispatch_processor);
  ThriftDispatchProcessorClass *parent_class =
    g_type_class_peek_parent (AA_AUTHORITY_PROCESSOR_GET_CLASS (self));

  process_function_def = g_hash_table_lookup (self->process_map, method_name);
  if (process_function_def != NULL)
  {
    g_free (method_name);
    dispatch_result = (*process_function_def->function) (self,
                                                         sequence_id,
                                                         input_protocol,
                                                         output_protocol,
                                                         error);
  }
  else
  {
    dispatch_result = parent_class->dispatch_call (dispatch_processor,
                                                   input_protocol,
                                                   output_protocol,
                                                   method_name,
                                                   sequence_id,
                                                   error);
  }

  return dispatch_result;
}

static void
aa_authority_processor_set_property (GObject *object,
                                     guint property_id,
                                     const GValue *value,
                                     GParamSpec *pspec)
{
  aaAuthorityProcessor *self = AA_AUTHORITY_PROCESSOR (object);

  switch (property_id)
  {
    case PROP_AA_AUTHORITY_PROCESSOR_HANDLER:
      if (self->handler != NULL)
        g_object_unref (self->handler);
      self->handler = g_value_get_object (value);
      g_object_ref (self->handler);
      break;
    default:
      G_OBJECT_WARN_INVALID_PROPERTY_ID (object, property_id, pspec);
      break;
  }
}

static void
aa_authority_processor_get_property (GObject *object,
                                     guint property_id,
                                     GValue *value,
                                     GParamSpec *pspec)
{
  aaAuthorityProcessor *self = AA_AUTHORITY_PROCESSOR (object);

  switch (property_id)
  {
    case PROP_AA_AUTHORITY_PROCESSOR_HANDLER:
      g_value_set_object (value, self->handler);
      break;
    default:
      G_OBJECT_WARN_INVALID_PROPERTY_ID (object, property_id, pspec);
      break;
  }
}

static void
aa_authority_processor_dispose (GObject *gobject)
{
  aaAuthorityProcessor *self = AA_AUTHORITY_PROCESSOR (gobject);

  if (self->handler != NULL)
  {
    g_object_unref (self->handler);
    self->handler = NULL;
  }

  G_OBJECT_CLASS (aa_authority_processor_parent_class)->dispose (gobject);
}

static void
aa_authority_processor_finalize (GObject *gobject)
{
  aaAuthorityProcessor *self = AA_AUTHORITY_PROCESSOR (gobject);

  thrift_safe_hash_table_destroy (self->process_map);

  G_OBJECT_CLASS (aa_authority_processor_parent_class)->finalize (gobject);
}

static void
aa_authority_processor_init (aaAuthorityProcessor *self)
{
  guint index;

  self->handler = NULL;
  self->process_map = g_hash_table_new (g_str_hash, g_str_equal);

  for (index = 0; index < 3; index += 1)
    g_hash_table_insert (self->process_map,
                         aa_authority_processor_process_function_defs[index].name,
                         &aa_authority_processor_process_function_defs[index]);
}

static void
aa_authority_processor_class_init (aaAuthorityProcessorClass *cls)
{
  GObjectClass *gobject_class = G_OBJECT_CLASS (cls);
  ThriftDispatchProcessorClass *dispatch_processor_class =
    THRIFT_DISPATCH_PROCESSOR_CLASS (cls);
  GParamSpec *param_spec;

  gobject_class->dispose = aa_authority_processor_dispose;
  gobject_class->finalize = aa_authority_processor_finalize;
  gobject_class->set_property = aa_authority_processor_set_property;
  gobject_class->get_property = aa_authority_processor_get_property;

  dispatch_processor_class->dispatch_call = aa_authority_processor_dispatch_call;
  cls->dispatch_call = aa_authority_processor_dispatch_call;

  param_spec = g_param_spec_object ("handler",
                                    "Service handler implementation",
                                    "The service handler implementation "
                                    "to which method calls are dispatched.",
                                    AA_TYPE_AUTHORITY_HANDLER,
                                    G_PARAM_READWRITE);
  g_object_class_install_property (gobject_class,
                                   PROP_AA_AUTHORITY_PROCESSOR_HANDLER,
                                   param_spec);
}
